declare module '*.png'

declare module '*.jpeg'

declare module '*.mp4'

declare module 'gl-transitions' {
  export interface GLTransition {
    name: string
    glsl: string
    paramsTypes: {
      [name: string]: string
    }
    defaultParams: {
      [name: string]: any
    }
    license: string
    updatedAt: string
    author: string
    createdAt: string
  }

  const transitions: GLTransition[]

  export default transitions
}

interface HTMLVideoElement {
  __hls__?: Hls
}
